# -*- coding: utf-8 -*-
import base64, struct

bmp_data = base64.b64decode(
    '')

print(bmp_data)
def bmp_info(data):
    bpm = struct.unpack('<ccIIIIIIHH', data[:30])
    while bpm[0] == b'B':
        if bpm[1] == b'M' or bpm[1] == b'A':
            return {
                'width': bpm[-4],
                'height': bpm[-3],
                'color': bpm[-1]
            }
        else:
            print('不是位图文件')



# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')